import React, {Component} from 'react';
import AddItem from './AddItem';
import ToDoList from './ToDoList';


class App extends Component {

 state ={
   items: [
     {itemName: 'Agatka', itemChecked: false},
     {itemName: 'Klaudynka', itemChecked: false},
     {itemName: 'Justynka', itemChecked: false},
   ]
 }

 handleAdd = (a) => {
   this.setState({
     items: this.state.items.concat({itemName: a, itemChecked: false}),
   })
 }

 render() {
  return (
    <>
   <AddItem add={this.HandleAdd}/>
   <ToDoList items={this.state.items}/>
   </>
  );
}





}


export default App;
